mapper.select:
select id, name, path, type, size, user_id, creator, parent_id, is_shared, create_time, update_time, content_type
from file
where id = #{id}

select id, name, path, type, size, user_id, creator, parent_id, is_shared, create_time, update_time, content_type
from file


        and user_id = #{userId}


        and parent_id = #{parentId}


        and is_shared = #{shared}

mapper.update:
update file
set

    name = #{name},


    is_shared = #{shared},

update_time = #{updateTime}
where id = #{id}
