mapper.select:
select id,
       name,
       type,
       icon,
       parent_id,
       resource_path,
       request_method,
       router_name,
       router_path,
       component_path,
       create_time,
       update_time
from permission


        and id = #{id}


        and name = #{name}


        and type = #{type}


        and icon = #{icon}


        and parent_id = #{parentId}


        and resource_path = #{resourcePath}


        and request_method = #{requestMethod}


        and router_name = #{routerName}


        and router_path = #{routerPath}


        and component_path = #{componentPath}
